svg.script:
const SVG_NS = 'http://www.w3.org/2000/svg';
const XHTML_NS = 'http://www.w3.org/1999/xhtml';

const div1 = document.createElementNS(XHTML_NS, 'div');
div1.style.display = 'contents';
div1.innerHTML = `<svg xmlns="${SVG_NS}" width="70%" height="70%">
  <rect x="50" y="50" width="300" height="100" fill="rgba(0,0,255,0.5)"></rect>
</svg>`;

const div2 = document.createElementNS(XHTML_NS, 'div');
div2.style.display = 'contents';
div2.innerHTML = `<svg xmlns="${SVG_NS}" width="300" height="200">
  <rect x="70" y="75" width="300" height="100" fill="rgba(0,255,0,0.5)"></rect>
</svg>`;

document.documentElement.insertBefore(div2, document.documentElement.firstChild);
document.documentElement.insertBefore(div1, div2);
