svg.script:
[
  'pathElement1',
  'pathElement2',
  'polygonElement1',
  'polygonElement2',
  'polylineElement1',
  'polylineElement2',
].forEach(elementId => {
  test(function() {
    let element = document.getElementById(elementId);
    assert_throws_dom("InvalidStateError", function() { element.getTotalLength(); });
  }, document.title + ', ' +
     document.getElementById(elementId).getAttribute('description'));
});

[
  'rectElement1',
  'rectElement2',
  'circleElement1',
  'circleElement2',
  'ellipseElement1',
  'ellipseElement2'
].forEach(elementId => {
  test(function() {
    let element = document.getElementById(elementId);
    assert_approx_equals(element.getTotalLength(), 0, 1e-5);
  }, document.title + ', ' +
     document.getElementById(elementId).getAttribute('description'));
});
